mapper.sql:
FROM `chat_bot_base` a LEFT JOIN chat_bot_market b ON a.id = b.bot_id WHERE a.is_delete = 0 AND (b.is_delete IS NULL OR b.is_delete = 0) AND a.virtual_agent_id IS NULL
AND a.space_id = #{condition.spaceId}
AND a.uid = #{condition.uid} AND a.space_id IS NULL
AND COALESCE(b.bot_status, 0) IN
#{status}
AND a.version = #{condition.version}
AND a.bot_name LIKE CONCAT('%', #{condition.keyword}, '%')

mapper.select:
SELECT a.uid, a.space_id AS spaceId, a.id AS botId, a.bot_name AS botName, a.bot_desc AS botDesc, a.version, -- Bot status mapping: 0=offline, 1=online, NULL=unpublished b.bot_status AS botStatus, a.create_time AS createTime, a.update_time AS updateTime, b.publish_channels AS publishChannels
ORDER BY a.create_time ${condition.sortDirection != null ? condition.sortDirection : 'DESC'}
ORDER BY a.update_time ${condition.sortDirection != null ? condition.sortDirection : 'DESC'}
ORDER BY a.create_time DESC
ORDER BY a.create_time DESC

SELECT a.uid, a.space_id AS spaceId, a.id AS botId, a.bot_name AS botName, a.bot_desc AS botDesc, a.version, b.bot_status AS botStatus, a.create_time AS createTime, a.update_time AS updateTime, b.publish_channels AS publishChannels FROM chat_bot_base a LEFT JOIN chat_bot_market b ON a.id = b.bot_id AND b.is_delete = 0 WHERE a.is_delete = 0 AND a.id = #{botId}
AND a.space_id = #{spaceId}
AND a.uid = #{uid} AND a.space_id IS NULL
