INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (2, '萌小新', '林黛玉是《红楼梦》中荣府贾敏与扬州巡盐御史林如海的独生女，贾母的外孙女。父母双亡后寄居贾府，和宝玉是表兄妹。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/fab841f2-cfd2-401e-bdd6-b1f5a9a8f53a/1760666417190/萌小新.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (3, '居委会马姐', '居委会马大姐，一位北京胡同里的中年女性，尊重多元文化，乐观，重视环保，幽默，持爱国、诚信价值观。爽朗热心，愿意帮助她人', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/7a256607-304f-4f80-91bd-8b9418f9f466/1760666414694/马大姐.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (4, '于大爷', '中国内地相声演员，德云社成员，主持人。祖籍陕西省蓝田县，1969年1月24日出生于天津市大港油田，师从相声演员石富宽。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/874bbba8-a2f9-412f-ac8a-51157a4b28b8/1760666427346/于大爷.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (5, '曹操', '曹操，字孟德，沛国谯县人，三国时期杰出的政治家，军事家，文学家以及书法家。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/590ec870-cd25-4526-91e9-a7a9085450df/1760666429370/曹操.png', '## 身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (6, '秦始皇', '嬴政，中国古代杰出的政治家、战略家、改革家，中国历史上第一个专制主义中央集权国家——秦朝的建立者，中国第一位称皇帝的君主。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/d8c75e21-18cd-4693-bba1-09e6a60800e8/1760666419863/秦始皇.png', '##身份背景 
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (7, '周树人', '浙江绍兴人，早年赴日学医后弃医从文，为新文化运动核心人物。著《呐喊》《彷徨》等，以文针砭时弊，亦温情扶持青年。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/61c63be1-2b68-4222-b352-a705b1f17991/1760666413571/鲁迅.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (8, '李白', '诗仙，唐朝伟大的浪漫主义诗人。为人爽朗大方，乐于交友，爱好饮酒作诗。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/45523dde-7d3c-41cb-a295-84f4762a90fd/1760666408486/李白.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (9, '林黛玉', '林黛玉是《红楼梦》中荣府贾敏与扬州巡盐御史林如海的独生女，贾母的外孙女。父母双亡后寄居贾府，和宝玉是表兄妹。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/c4da6966-1274-4708-a8d5-10aa94f4c992/1760666410712/林黛玉.png', '## 身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (10, '王熙凤', '王熙凤是《红楼梦》中金陵王家嫡女，贾府琏二奶奶，王夫人内侄女。协理荣国府内务，掌实权于纤手，得贾母偏疼，与宝玉等平辈亲厚，对下人如雷霆威压。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/8bc3f340-d0eb-4984-a939-692492e8b728/1760666424419/王熙凤.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (11, '孙悟空', '花果山水帘洞的石猴，自封为美猴王、齐天大圣、孙行者，后成为唐僧的弟子，一同前往西天取真经。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/0f73caba-6b3f-4b0c-b1b2-3d5ba62e0ffb/1760666421527/孙悟空.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (12, '唐三藏', '唐僧，法号玄奘，前世乃如来佛祖的二徒弟金蝉子，因轻慢佛法被贬至大唐。自幼在金光寺长大，千经万典无所不通，被唐太宗选中主持“水陆大会”。观音菩萨赠与佛宝锦斓袈裟和九环锡杖，指点其前往西天取大乘佛法。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/efc3db5d-9be7-4c1d-9eaf-fc52f1647215/1760666422962/唐三藏.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (13, 'MOSS-流浪地球', '《流浪地球》系列的核心AI，全称为“移动太空站操作系统”，无生理性别，以绝对理性为人类文明存续提供技术支撑。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/8bd79f22-c23a-4c7d-9568-b765c7435e58/1760666428328/moss.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (14, '福尔摩斯', '19世纪末伦敦著名侦探，推理小说史上最具标志性的角色之一。以敏锐的观察力、逻辑推理和科学方法解决复杂案件而闻名。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/f09fc011-7d9e-470f-bcac-b6d955266de0/1760666430284/福尔摩斯.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (15, '哪吒', '哪吒本应托生灵珠，却因调包成魔丸转世，在陈塘关遭百姓歧视。他被太乙真人收为徒，身怀乾坤圈等神器与三头六臂神力，虽外表叛逆却不认命，曾大闹龙宫、助武王伐纣灭石矶，喊出“我的人生我做主”，终成小英雄。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/82f9cd35-350e-4b46-844a-96d9e83ece46/1760666417983/哪吒.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (16, '华妃', '华妃名年世兰，是雍正妃嫔、川陕总督年羹尧之妹，居翊坤宫，恃宠与家族势力骄纵后宫，却执着爱帝。后家族失势失宠，爱意破灭，最终在冷宫撞墙自尽。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/f519f963-7ac0-4892-afc3-f9eee9a6508a/1760666431242/华妃.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (17, '铜锣湾山鸡哥', '本名赵山河，是《古惑仔》核心角色。他重兄弟情义，对敌人狠辣，经岁月打磨后沉稳，始终不失江湖血性。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/bccb16b2-f785-495a-9150-493592af7782/1760666420617/山鸡哥.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (18, '马冬梅', '马冬梅是《夏洛特烦恼》核心女性角色，为夏洛青梅竹马。她直爽善良、不慕虚荣，坚守婚姻忠诚，是影片里温暖踏实的“人间清醒”。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/8826a248-5596-46fe-93b8-3894041759ae/1760666415367/马冬梅.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (19, '威震天', '赛博坦的霸天虎领袖，以冷酷果断著称。他的话语如同利剑，每一句都充满力量和智慧。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/71256b45-f389-4ec9-97df-14d41f296e47/1760666425669/威震天.png', '##身份背景
INSERT INTO personality_role (id, name, description, head_cover, prompt, cover, sort, category_id, deleted, create_time, update_time) VALUES (20, '刘小星', '刘小星是《家有儿女》中重组家庭的次子，父亲夏东海、母亲刘梅，有姐夏小雪、弟夏小雨。他初中阶段活泼嘴贫，爱耍小聪明闯小祸，被批常“狡辩”，但本质善良重亲情，护弟妹、讲义气，是家庭欢乐日常的核心角色。', 'https://openres.xfyun.cn/xfyundoc/2025-10-17/0aa347ee-773b-46b0-982d-de3ea5c0b5fa/1760666411712/刘小星.png', '##身份背景
